import React from 'react';
import '../reports/css/OrderDetails.css'; // Import the CSS file

const OrderDetails = () => {
  // Dummy data for demonstration
  const orderDetails = {
    orderId: 'ORD001',
    customerMobile: '123-456-7890',
    orderTotal: '150.75',
    itemCount: 5,
    paymentMode: 'Credit Card',
    orderStatus: 'Completed',
  };

  const orderItems = [
    { product: 'Laptop', quantity: 1, salePrice: '1200.00', brand: 'Dell', type: 'Electronics' },
    { product: 'Mouse', quantity: 2, salePrice: '25.00', brand: 'Logitech', type: 'Electronics' },
    { product: 'Keyboard', quantity: 1, salePrice: '75.00', brand: 'Razer', type: 'Electronics' },
  ];

  return (
    <div className="order-details-container">
      <h2>Order Details Screen</h2>

      <div className="order-details-grid">
        <div className="detail-item">
          <strong>Order ID:</strong> {orderDetails.orderId}
        </div>
        <div className="detail-item">
          <strong>Customer Mobile:</strong> {orderDetails.customerMobile}
        </div>
        <div className="detail-item">
          <strong>Order Total:</strong> ${orderDetails.orderTotal}
        </div>
        <div className="detail-item">
          <strong>Item Count:</strong> {orderDetails.itemCount}
        </div>
        <div className="detail-item">
          <strong>Payment Mode:</strong> {orderDetails.paymentMode}
        </div>
        <div className="detail-item">
          <strong>Order Status:</strong> {orderDetails.orderStatus}
        </div>
      </div>

      <h3>Order Items</h3>
      <table className="order-items-table">
        <thead>
          <tr>
            <th>Product</th>
            <th>Quantity</th>
            <th>Sale Price</th>
            <th>Brand</th>
            <th>Type</th>
          </tr>
        </thead>
        <tbody>
          {orderItems.map((item, index) => (
            <tr key={index}>
              <td>{item.product}</td>
              <td>{item.quantity}</td>
              <td>${item.salePrice}</td>
              <td>{item.brand}</td>
              <td>{item.type}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default OrderDetails;
