import React from 'react';
import { useNavigate } from 'react-router-dom';
import './HomePage.css';

const HomePage = () => {
  const navigate = useNavigate();
  const loggedInUser = "John Doe"; // This would typically come from authentication context

  const menuItems = [
    {
      id: 'register',
      title: 'Register',
      icon: 'ðŸ›’',
      path: '/register',
      color: '#4CAF50'
    },
    {
      id: 'store-status',
      title: 'Store Status',
      icon: 'ðŸª',
      path: '/store-status',
      color: '#2196F3'
    },
    {
      id: 'pricebook',
      title: 'Pricebook',
      icon: 'ðŸ’°',
      path: '/pricebook',
      color: '#FF9800'
    },
    {
      id: 'vendors',
      title: 'Vendors',
      icon: 'ðŸšš',
      path: '/vendors',
      color: '#9C27B0'
    },
    {
      id: 'users',
      title: 'Users',
      icon: 'ðŸ‘¥',
      path: '/users',
      color: '#F44336'
    },
    {
      id: 'time-clock',
      title: 'Time Clock',
      icon: 'â°',
      path: '/time-clock',
      color: '#607D8B'
    }
  ];

  const handleMenuClick = (path) => {
    navigate(path);
  };

  return (
    <div className="homepage">
      <div className="header">
        <h1 className="welcome-title">Welcome</h1>
      </div>
      
      <div className="menu-grid">
        {menuItems.map((item) => (
          <button
            key={item.id}
            className="menu-button"
            style={{ '--button-color': item.color }}
            onClick={() => handleMenuClick(item.path)}
          >
            <div className="menu-icon">{item.icon}</div>
            <div className="menu-title">{item.title}</div>
          </button>
        ))}
      </div>
      
      <div className="footer">
        <p className="user-greeting">Hi, {loggedInUser}</p>
      </div>
    </div>
  );
};

export default HomePage;
